(1033, '文章测试二ddd', '<p>\r\n	<strong>文章测试二</strong> \r\n</p>\r\n<p>\r\n	<strong><img src="/public\\uploads\\6876-5bey92.jpg" alt="" /><br />\r\n</strong> \r\n</p>\r\n<p>\r\n	<strong><br />\r\n</strong> \r\n</p>\r\n<p>\r\n	<strong>代码测试:</strong> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<pre class="brush:js">exports.add=function(req,res){\r\n	//查找出所有的category\r\n	category.findById(null,function(err,rows){\r\n		if(err){\r\n			console.log(err);\r\n			return res.send(500,''内部错误'');\r\n		}\r\n		res.render(''admin/add'',{category:rows});\r\n	});\r\n}</pre>\r\nasdfasdf<br />\r\n<p>\r\n	<br />\r\n</p>', '1387092437485', '1387093935278', 4);
